const btnStyle = {
    backgroundColor : '#ff0000',
    color : '#fff',
    borderRadius : '50%',
    float : 'right',
    padding : '5px',
    border : 'none',
    cursor : 'pointer',
}

//Props Types
Todositem.propTypes = {
    todo: PropTypes.object.isRequired
}
export default Todositem
